#!/bin/bash

function multisess_subjlist(){ pid=$1; visit=$2; session=$3; num_runs=$4;  subjectlist_path="/oak/stanford/groups/menon/projects/daelsaid/2019_met/data/subjectlist"; compiled_subj_files="/oak/stanford/groups/menon/projects/daelsaid/2019_met/compiled_subjlists"; sed 1d ${subjectlist_path}/subjectlist_multisess.csv >> ${compiled_subj_files}/compiled_multisess_subjectlist.csv; rm ${subjectlist_path}/subjectlist_multisess.csv 2>/dev/null ;     runs=($(seq 1 1 4)); echo ${runs[@]};echo -e "PID,Visit,Session,Run1,Run2,Run3,Run4,Contrast_file" >> ${subjectlist_path}/subjectlist_multisess.csv;  echo -e "${pid}","${visit}","${session}",`for num in $(seq 0 3 ); do echo -e sym${runs[$num]} |grep -v "sym"',';done` "contrasts_${num_runs}.mat" >> ${subjectlist_path}/subjectlist_multisess.csv; }
